import {useState} from 'react'

function App() {

  const [head, setHead] = useState(" ");
  const[count, setCount]=useState(0);

 function handleChange(){
   setCount(count+1);
   if(count%2==0){
     setHead(" ")
   }
   setHead("Successfully clicked")

 }
  return (
  <>

    <h1>Hello</h1>
    <button onClick={handleChange}> Click Me </button>
    <h3>{head}</h3>


  </>

    )
}

export default App;
